import React from 'react';
import { BrowserRouter, Routes, Route, useLocation } from 'react-router-dom';
import Home from './Pages/Home.jsx';
import Products from './Pages/Products.jsx';
import LoginComponent from './Pages/Login.jsx';
import Navbar from './Components/Navbar.jsx'
import Footer from './Components/Footer.jsx';
import UserOrder from './Pages/UserOrder.jsx';
import ForgotPassword from './Pages/ForgotPassword.jsx';
import VerifyOTP from './Pages/VerifyOTP.jsx';
import ResetPassword from './Pages/ResetPassword.jsx';
import Register from './Pages/Register.jsx';
import Admin from './Pages/Admin.jsx';
import { UserProvider } from './context/UserContext';
import ProtectedRoute from './Components/ProtectedRoute.jsx';
import { ThemeProvider } from './context/ThemeContext';


const AppContent = () => {
  const location = useLocation();
  const isAuthPage = ['/login', '/register', '/forgotpassword', '/verifyotp', '/resetpassword'].includes(location.pathname);

  return (
    <>
      <Navbar />
      <div>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<LoginComponent />} />
          <Route path="/register" element={<Register />} />
          <Route path="/forgotpassword" element={<ForgotPassword />} />
          <Route path="/verifyotp" element={<VerifyOTP />} />
          <Route path="/resetpassword" element={<ResetPassword />} />

          {/* Protected Routes */}
          <Route path="/products" element={
            <ProtectedRoute>
              <Products />
            </ProtectedRoute>
          } />
          <Route path="/userorder" element={
            <ProtectedRoute>
              <UserOrder />
            </ProtectedRoute>
          } />
          <Route path="/Admin" element={
            <ProtectedRoute>
              <Admin />
            </ProtectedRoute>
          } />
        </Routes>
      </div>
      {!isAuthPage && <Footer />}
    </>
  );
};

function App() {
  return (
    <ThemeProvider>
      <UserProvider>
        <BrowserRouter>
          <AppContent />
        </BrowserRouter>
      </UserProvider>
    </ThemeProvider>
  );
}

export default App;
